import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css'
import PropertyRecords from './abis/PropertyRecords.json'

function App() {
  const [provider, setProvider] = useState(null);
  const [address, setAddress] = useState('');

  useEffect(() => {
    async function init() {
      // Check if MetaMask is installed
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(provider);

        try {
          // Request access to the user's MetaMask account
          await window.ethereum.request({ method: 'eth_requestAccounts' });

          // Get the current account address
          const accounts = await provider.listAccounts();
          setAddress(accounts[0]);

          // Listen for changes to the current account
          window.ethereum.on('accountsChanged', (accounts) => {
            setAddress(accounts[0]);
          });
        } catch (error) {
          console.error(error);
        }
      }
    }
    init();
  }, []);

  useEffect(() => {
    console.log(`Current account: ${address}`);
    setAddress(address)
  }, [address]);

  function handleConnectWallet() {
    window.ethereum.request({ method: 'eth_requestAccounts' }).then((accounts) => {
      setAddress(accounts[0]);
    }).catch((error) => {
      console.error(error);
    });
  }

  return (
    <div className="App">
      <h1 className='text-3xl p-5 font-bold'>Home Page</h1>
      {address ? (
        <div className='font-bold'>Connected Account: {address}</div>
      ) : (
        <button className="button" onClick={handleConnectWallet}>Connect Wallet</button>
      )}
    </div>
  );
}

export default App;
