import React from 'react';
import { useState, useEffect } from 'react';

const Page1 = ({ provider, contract }) => {
  const [ownerName, setOwnerName] = useState("");
  const [ownerWalletAddress, setOwnerWalletAddress] = useState("");
  const [landLocation, setLandLocation] = useState("");
  const [propertyValue, setPropertyValue] = useState('');
  const [surveyNumber, setSurveyNumber] = useState('');
  const [image, setImage] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const signer = provider.getSigner();

      const tx = await contract.connect(signer).registerProperty(
        ownerName,
        ownerWalletAddress,
        landLocation,
        propertyValue,
        surveyNumber,
        image
      );
      await tx.wait();
      alert("Property registered successfully");
    } catch (error) {
      console.log(error);
      alert("Failed to register");
    }
  };

  return (
    <div className='flex flex-row justify-center items-center'>
      <h2>Project Registration</h2>
      <form className='flex-flex-row justify-center items-center' onSubmit={handleSubmit}>
        <label htmlFor="owner-name">Owner Name</label>
        <input type="text" id="owner-name" value={ownerName} onChange={(event) => setOwnerName(event.target.value)} required />

        <label htmlFor="owner-wallet-address">Owner Wallet Address</label>
        <input type="text" id="owner-wallet-address" value={ownerWalletAddress} onChange={(event) => setOwnerWalletAddress(event.target.value)} required />

        <label htmlFor="land-location">Land Location</label>
        <input type="text" id="land-location" value={landLocation} onChange={(event) => setLandLocation(event.target.value)} required />

        <label htmlFor="property-value">Property Value</label>
        <input type="number" id="property-value" value={propertyValue} onChange={(event) => setPropertyValue(event.target.value)} required />

        <label htmlFor="survey-number">Survey Number</label>
        <input type="text" id="survey-number" value={surveyNumber} onChange={(event) => setSurveyNumber(event.target.value)} required />

        <label htmlFor="image">Image</label>
        <input type="file" id="image" onChange={(event) => setImage(event.target.files[0])} required />

        <button type="submit">Register Property</button>
      </form>
    </div>
  );
}

export default Page1;
