import React, { useState } from "react";
import { TextField, Button } from "@mui/material";
import { styled } from "@mui/material/styles";
import "./RegisterProperty.css";

const RegisterProperty = ({ provider, contract }) => {
  const [ownerName, setOwnerName] = useState("");
  const [ownerWalletAddress, setOwnerWalletAddress] = useState("");
  const [landLocation, setLandLocation] = useState("");
  const [propertyValue, setPropertyValue] = useState(0);
  const [surveyNumber, setSurveyNumber] = useState(0);
  const [image, setImage] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const signer = provider.getSigner();
      /* console.log(signer) */
      const tx = await contract.connect(signer).registerProperty(
        ownerName,
        ownerWalletAddress,
        landLocation,
        propertyValue,
        surveyNumber,
        image
      );
      await tx.wait();
      alert("Property registered successfully");
    } catch (error) {
      console.log(error);
      alert("Failed to register");
    }
  };

  return (
    <StyledContainer>
      <form onSubmit={handleSubmit}>
        <h1>Register Property</h1>
        <StyledTextField
          label="Owner Name"
          variant="outlined"
          value={ownerName}
          onChange={(event) => setOwnerName(event.target.value)}
        />
        <StyledTextField
          label="Wallet Address"
          variant="outlined"
          value={ownerWalletAddress}
          onChange={(event) => setOwnerWalletAddress(event.target.value)}
        />
        <StyledTextField
          label="Land Location"
          variant="outlined"
          value={landLocation}
          onChange={(event) => setLandLocation(event.target.value)}
        />
        <StyledTextField
          label="Property Value"
          variant="outlined"
          type="number"
          value={propertyValue}
          onChange={(event) => setPropertyValue(event.target.value)}
        />
        <StyledTextField
          label="Survey Number"
          variant="outlined"
          type="number"
          value={surveyNumber}
          onChange={(event) => setSurveyNumber(event.target.value)}
        />
        <StyledTextField
          label="Image"
          variant="outlined"
          value={image}
          onChange={(event) => setImage(event.target.value)}
        />
        <StyledButton
          type="submit"
          variant="contained"
          size="large"
          disableElevation
        >
          Register Property
        </StyledButton>
      </form>
    </StyledContainer>
  );
};

const StyledContainer = styled("div")({
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  padding: "2rem",
});

const StyledTextField = styled(TextField)({
  width: "100%",
  marginBottom: "1rem",
});

const StyledButton = styled(Button)({
  marginTop: "1rem",
  background: "#1a73e8",
  color: "white",
  "&:hover": {
    background: "#0d47a1",
  },
});

export default RegisterProperty;
